import React, { useState } from 'react'
import { Map, Marker, InfoWindow, GoogleApiWrapper } from 'google-maps-react'


const mapStyles = {
  width: 'calc(100vw - 600px)',
  height: '90vh',
};

const containerStyle = {
  width: '100%',
  height: '100%',
  position: 'relative',
}

function MapInstance(props) {
const markers = []

  return (
    <Map
      google={props.google}
      zoom={12}
      style={mapStyles}
      containerStyle={containerStyle}
      initialCenter={{ lat: 35.2271, lng: -80.8431}}>

      markers = {props.therapists.map(therapist =>
        { if (therapist.location) {
          return (
            <Marker
            key={therapist.id}
            name={therapist.name}
            position={{ lat: therapist.location.lat, lng: therapist.location.lng }} />
          )
        }}
      )}

      {props.therapists.map(therapist =>
        { if (therapist.location) {
          return (
            <InfoWindow
              marker= {markers[0]}
              visible={true}
              position={{ lat: therapist.location.lat, lng: therapist.location.lng }} />
          )
        }}
      )}

    </Map>
  )
}

export default GoogleApiWrapper({
  apiKey: process.env.GOOGLE_MAPS_API_KEY
})(MapInstance);

//To-Do Secure API Key
