import { useState } from 'react'
import { makeStyles } from '@material-ui/core'
import Box from '@material-ui/core/Box'
import Option from './Option'

const useStyles = makeStyles(theme => ({
  selectOptions: {
    width: 300,
    background:'#fff',
    borderRadius:4,
    border: "1px solid #ddd",
    boxShadow: '0px 5px 8px -3px rgba(0,0,0,0.14)',
  },
  Button: {
    marginRight:15,
  }
}));

const Select = props => {
  const classes = useStyles()
  const [selected, setSelected] = useState(null)

  const handleChange = (e) => {
    setSelected(e.target.value)
  }

  return (
    <form onChange={handleChange} className={classes.selectOptions}>
      {props.options.map((option, i) => (
        <Option id={i} type={props.type} value={option}>{option}</Option>
      ))}
      <Option id={1} value="individual" name="clientType" isSelected={selected}/>
      <Option id={2} value="couple" name="clientType" type={props.type} isSelected={selected} />
      <Option id={3} value="family" name="clientType"  type={props.type} isSelected={selected} />
    </form>
  )
}

export default Select
