import React, { useEffect, useRef, useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Marker from './Marker'

const useStyles = makeStyles({
  root: {
    display: 'inline-block',
    height: '90vh',
    width: 'calc(100vw - 600px)'
  },
});

const Map = props => {
  const classes = useStyles()
  const [ map, setMap ] = useState({})
  const [ markers, setMarkers ] = useState({})
  const mapRef = useRef(null)

  const initMap = () => {
    const map = new google.maps.Map(mapRef.current, {
      center: { lat: 35.2271, lng: -80.8431 },
      zoom: 12
    });
    setMap(map)

    const markers = []
    props.therapists.forEach((therapist) => {
      if (therapist.location) {
        let marker = new google.maps.Marker({position: therapist.location , map: map})
        markers.push(marker)
      }
    })
    setMarkers(markers)
  }

  useEffect(() => {
    initMap()
    console.log(markers)
    return () => {
    }
  }, [])



  // const selectMarkers = () => {
  //   markers[0].setAnimation(google.maps.Animation.BOUNCE)
  // }
  //
  // useEffect(() => {
  //   selectMarkers()
  //   return () => {
  //   }
  // }, [props.selected])

  return (
    <div ref={mapRef} className={classes.root}>
      <Marker map= {map}/>
    </div>
  )
}

export default Map
