export const Capitalize = input => {
  if (typeof(input) === 'string') {
    return input.charAt(0).toUpperCase() + input.slice(1)
  } else {
    input.map(string => {
      string.charAt(0).toUpperCase() + string.slice(1)
    })
  }
}
