import React, { useState } from "react"
import { makeStyles } from '@material-ui/core/styles'
import ListItem from "@material-ui/core/ListItem"
import ListItemText from "@material-ui/core/ListItemText"
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import CardMedia from '@material-ui/core/CardMedia'
import Box from '@material-ui/core/Box'
import Divider from '@material-ui/core/Divider'
import Group from '@material-ui/icons/Group'
import Grid from '@material-ui/core/Grid'
import InsuranceIcon from '@material-ui/icons/Payment'
import PinIcon from '@material-ui/icons/Room'


const Listing = props => {
  const classes = useStyles()
  const therapist = props.therapist

  function handleClick() {
    props.setSelected(props.id)
    if (!props.showMidbar)props.setShowMidbar(true)
  }

  return (
      <ListItem className={classes.root} button selected={therapist.id === props.selected} onClick={handleClick}>
        <div className={classes.imageCropper}>
          <img src={'/therapist-images/' + therapist.image} className={classes.image} />
        </div>

        <div className={classes.content}>
          <h2 className={classes.name}>
            {therapist.first_name} {therapist.last_name}
          </h2>
          <h3 className={classes.title}>
            {therapist.title}
          </h3>
          <h4 className={classes.specialties}>
            {therapist.specialties.join(', ') + '...'}
          </h4>
          <span className={classes.insurance}>
            <InsuranceIcon className={classes.insuranceIcon}/>
            {therapist.insurance.length ? "Accepts some insurance" : "Does not accept insurance"}
          </span>
        </div>

        <span className={classes.neighborhood}>
          <PinIcon className={classes.pinIcon}/>
          {therapist.neighborhood}
        </span>
      </ListItem>
  )
}

const useStyles = makeStyles(theme => ({
  root: {
    overflow: 'hidden',
    background: "white",
    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
    borderRadius: 6,
    margin: '15px 0px',
    listStyleType: 'none',
  },
  imageCropper: {
    display:'inline-block',
    width: 90,
    height: 90,
    minWidth: 85,
    position: 'relative',
    overflow: 'hidden',
    borderRadius: '50%',
    marginRight:20,
  },
  image: {
    display: 'inline',
    margin: '0 auto',
    marginRight: '-25%',
    height: 'auto',
    width: '100%',
  },
  content: {
    display:'inline-block',
    verticalAlign: 'middle',
    overflow:'hidden'
  },
  name: {
    fontSize: 20,
    fontWeight:400,
    margin: 0,
    paddingTop:18,
    lineHeight:'16px',
  },
  title: {
    [theme.breakpoints.down('sm')]: {
      fontSize: 14,
    },
    paddingTop:6,
    fontSize: 16,
    fontWeight:400,
    margin: 0,
    lineHeight:'16px',
    fontWeight:'300'
  },
  specialties: {
    [theme.breakpoints.down('sm')]: {
      fontSize: 12,
    },
    paddingTop:12,
    fontSize: 15,
    fontWeight:600,
    margin: 0,
    lineHeight:'16px',
  },
  insurance: {
    [theme.breakpoints.down('sm')]: {
      fontSize: 12,
    },
    display:'block',
    paddingTop:12,
    paddingBottom:12,
    fontSize:14,
    lineHeight:'14px',
    color:'#565656',
  },
  insuranceIcon: {
    fontSize:18,
    verticalAlign:'middle',
    marginRight:10,
  },
  neighborhood: {
    position:'absolute',
    top:20,
    right:30,
    color:'#565656',
    fontSize:15,
  },
  pinIcon: {
    position:'relative',
    top:'-1px',
    marginRight:5,
    verticalAlign: 'bottom',
    fontSize:18,
  },

}));

export default Listing
