import React, { useState } from "react"
import { makeStyles } from '@material-ui/core/styles'
import ListItem from "@material-ui/core/ListItem"
import ListItemText from "@material-ui/core/ListItemText"
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import CardMedia from '@material-ui/core/CardMedia'
import Box from '@material-ui/core/Box'
import Divider from '@material-ui/core/Divider'
import Icon from '@material-ui/core/Icon'


const Listing = props => {
  const classes = useStyles()

  function handleClick() {
    props.setSelected(props.id)
    if (!props.showMidbar)props.setShowMidbar(true)
  }

  return (
    <div>
      <ListItem className={classes.root} button selected={props.id == props.selected ? true : false } onClick={handleClick}>
        <div className={classes.imageContainer}>
          <CardMedia image={'/therapist-images/' + props.image} className={classes.listingImage} />
        </div>
        <ListItemText className={classes.contentContainer}>
          <Typography  className={classes.title} variant="h5" component="h2">
            {props.name} {props.surname}
          </Typography>
          <Typography  className={classes.subtitle} color="textSecondary">
            Marriage Counselor
          </Typography>
          <Box>
            <Typography  className={classes.subtitle} color="textSecondary">
              <Icon>school</Icon> MSW, LCSW
            </Typography>
          </Box>
        </ListItemText>
      </ListItem>
      <Divider />
    </div>
  )
}

const useStyles = makeStyles({
  root: {
    overflow: 'hidden',
  },
  selected: {
    background:'red',
  },
  subtitle: {
    margin: 0,
    color:"#0000008a",
    fontSize:"14px",
    paddingBottom:8
  },
  title: {
    fontSize:"16px"
  },
  imageContainer: {
    width: 110,
    height: 110,
    padding:18,
    display:'inline-box',
    verticalAlign: 'top',
  },
  contentContainer: {
    width: 390,
    height: 120,
    display:'inline-box',
    verticalAlign: 'top',
    overflow:'hidden'
  },
  listingImage: {
    width: '100%',
    borderRadius:'50%',
  },
  chip: {
    position:'absolute',
    top:15,
    right:15,
  },

});

export default Listing
