import { useState, useEffect, useRef } from 'react'
import Button from "@material-ui/core/Button"
import IconButton from "@material-ui/core/IconButton"
import SelectOptions from './SelectOptions'
import Box from '@material-ui/core/Box'
import { makeStyles } from '@material-ui/core/styles'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  container: {
    display:"inline-block",
    position:"relative",
  },
  button: {
    marginRight:15,
    minWidth: 200,
    textAlign:"left",
    borderRadius:20,
    fontSize:13,
    padding:"2px 25px 2px",
    justifyContent:"normal",

  },
  expandIcon: {
    position: "absolute",
    right: 8,
  },
}));

const Filter = props => {
  const classes = useStyles()
  const [open, setOpen] = useState(false)
  const node = useRef()

  useEffect(() => {
    document.addEventListener("mousedown", handleClick)

    return () => {
      document.removeEventListener("mousedown", handleClick)
    }
  }, [])

  const handleClick = e => {
    if (node.current.contains(e.target)) {
    return;
    }
    console.log("close this")
  }

  return (
    <Box className={classes.container}>
      <Button
        className={classes.button}
        onClick={e => setOpen(!open)}
        size="small"
        color="secondary"
        variant={props.activeFilter ? "contained" : "none"}
      >
        { props.activeFilter ?
          `${props.label}: ${props.activeFilter}` :
          `Choose a ${props.label}`
        }
        <Icon className={classes.expandIcon}>expand_more</Icon>
      </Button>

      {open &&
      <SelectOptions ref={node} selected={props.activeFilter} options={props.options} filter={props.filter} onChange={props.onChange} type="radio"/>}
    </Box>
    )
}

export default Filter
