import React, { useEffect } from "react"
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import Button from '@material-ui/core/Button'
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography'
import Avatar from '@material-ui/core/Avatar'

const useStyles = makeStyles({
  root: {
    position: 'relative',
    display: 'inline-block',
    height: '100vh',
    width: '500px',
    background: "white"
  },
  hide: {
    display: "none",
  },
  closeButton: {
   position: 'absolute',
   right: 5,
   top: 5,
   color: "grey",
 },
 imageContainer: {
   display: "inline-block",
   verticalAlign: "bottom"
 },
 image: {
   width: "100px",
   height: "100px"
 },
 name: {
   fontSize: "26px"
 },
 description: {
   fontSize: "15px"
 }
});

const Midbar = props => {
  const classes = useStyles()

  return (
    <Box className={props.showMidbar ? classes.root : classes.hide}>
      <Box mt={6} mx={3} display="flex" flexDirection="column" alignItems="center">

            <Box className={classes.imageContainer}>
              <Avatar src={'/therapist-images/' + props.therapist.image} className={classes.image}/>
            </Box>

            <Box display="inline-block" height="100%">
              <Typography gutterBottom color="textSecondary">
                MSW, LCSW
              </Typography>

              <Typography className={classes.name}>
                {props.therapist.name} {props.therapist.surname}
              </Typography>

              <Typography color="textSecondary">
                Couples therapist
              </Typography>
            </Box>

            <Box mt={3}>
              <Box display="flex" justifyContent="center">
                <Typography className={classes.description}>
                  Do you want help with your life, your work or your relationships? Letâ€™s collaborate to lighten the obstacles to your best life! I offer individual therapy, couples therapy, crisis intervention, pre-marital coaching, family therapy, trauma-informed therapy, personal coaching, and work-life balance coaching. I use best practices that evolve clinically following advances in the research.
                </Typography>
              </Box>

              <Box display="flex" justifyContent="center" mt={3}>
                <Button variant="contained" color="secondary">
                  Schedule a Conversation
                </Button>
              </Box>
            </Box>

            <IconButton aria-label="close" className={classes.closeButton} onClick={() => props.setShowMidbar(false)}>
              <CloseIcon />
            </IconButton>

      </Box>
    </Box>
  )

}

export default Midbar
