import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect'
import SelectMultiple from './SelectMultiple'
import Button from '@material-ui/core/Button'
import Icon from '@material-ui/core/Icon'
import SelectOptions from './SelectOptions'

const useStyles = makeStyles(theme => ({
  root: {
    background:'#fff',
    padding:15,
    borderBottom: "1px solid #ddd",
  },
  Button: {
    marginRight:15,
  }
}));

const Filters = props => {
  const classes = useStyles()

  return (
    <Box className={classes.root}>
      <SelectOptions options={props.catalogue.clientTypes} filter={props.filters.clientType} name="clientType" onChange={props.onChange} type="radio"/>
      <Button className={classes.Button} color="secondary" variant="contained">Client: Individual <Icon>expand_more</Icon></Button>
      <Button className={classes.Button} color="secondary" variant="outlined">Specialty: All <Icon>expand_more</Icon></Button>
      <Button className={classes.Button} color="secondary" variant="outlined">Treatment: All <Icon>expand_more</Icon></Button>
      <Button className={classes.Button} color="secondary" variant="outlined">Price: All <Icon>expand_more</Icon></Button>
    </Box>
  )
}

export default Filters

