import { makeStyles } from '@material-ui/core'
import Box from '@material-ui/core/Box'

const useStyles = makeStyles(theme => ({
  option: {
    width: "100%",
    padding:15,
    cursor: "pointer",
    display:"block",
    "&:hover":{
      background: 'lightblue',
    },
  },
  selected: {
    color: "blue",
  },
  label: {
    paddingLeft:20,
  }
}));

const Option = props => {
  const classes = useStyles()

  return (
    <label className={`${classes.option} ${props.isSelected == props.value && classes.selected}`} name={props.name}>
      <input checked type={props.type} value={props.value} name={props.name} />
      <span className={classes.label}>{props.value}</span>
    </label>
  )
}

export default Option
