import Button from "@material-ui/core/Button"
import IconButton from "@material-ui/core/IconButton"
import SelectOptions from './SelectOptions'
import Box from '@material-ui/core/Box'
import { makeStyles } from '@material-ui/core/styles'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  container: {
    display:"inline-block",
    minWidth: 220,
    borderRadius:20,
    paddingLeft:"40px",
    background: theme.palette.primary.main,
    marginRight:15,
    position: "relative"
  },
  button: {
    borderRadius: "0 20px 20px 0",
    textAlign:"left",
    width: "100%",
    fontSize:13,
    justifyContent:"normal",
    background: "transparent",
    border: "none",
    cursor: "pointer",
    color: "white",
    padding:"4px 0"
  },
  expandIcon: {
    position: "absolute",
    right: 8,
    color: "white",
    verticalAlign:"middle",
  },
  closeIcon: {
    fontSize:18,
    color:"white"
  },
  closeButton: {
    position:"absolute",
    left: 0,
  }
}));

const Filter = props => {
  const classes = useStyles()

  return (
    <Box className={classes.container}>
      { props.activeFilter && <IconButton className={classes.closeButton}><Icon className={classes.closeIcon}>close</Icon></IconButton> }
      <button
        className={classes.button}
        onClick={() => props.showOptions(props.filter)}
        size="small"
        color="secondary"
        variant="contained"
      >
        { props.activeFilter ?
          `${props.label}: ${props.activeFilter}` :
          props.label
        }
        <Icon className={classes.expandIcon}>expand_more</Icon>
      </button>
      <SelectOptions options={props.options} visible={props.visible} filter={props.filter} onChange={props.onChange} type="radio"/>
    </Box>
    )
}

export default Filter
