import { useState, useEffect } from "react"
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect';

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    height:70,
    background: "transparent",
    padding:15,
  },
}));

const Filters = props => {
  const classes = useStyles()

  const [filters, setFilters] = useState({
    clientType: "individual",
    specialty: "depression"
  })

  const onChange = (filter, event) => {
    setFilters({
      ...filters,
      [filter]: event.target.value
    })
  }

  return (
    <Box className={classes.root}>
      <SingleSelect filter="clientType" options={props.catalogue.clientTypes} onChange={onChange}/>
      <span>{filters.clientType}</span>
      <SingleSelect filter="specialty" options={props.catalogue.specialties} onChange={onChange}/>
      <span>{filters.specialty}</span>
    </Box>
  )
}

export default Filters
