import { useState, useEffect } from "react"
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import Button from '@material-ui/core/Button'
import Grid from '@material-ui/core/Grid'
import Icon from '@material-ui/core/Icon';
import Typography from '@material-ui/core/Typography'



const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    height:70,
    background: "#FFF",
    display: 'inline-block',
    borderBottom: "1px solid #ccc",
    padding:15,
  },
  filter: {
    marginRight:15,
  }
}));

const FilterBar = props => {
  const classes = useStyles()
  const [openFilter, setOpenFilter] = useState(true)

  useEffect((filter) => {
    setOpenFilter(filter)

    return () => {

    }
  }, [])

  return (
      <Box className={classes.root} p={1}>
        <Button className={classes.filter} variant="outlined" color="secondary">Client Type: All<Icon>expand_more</Icon></Button>
        <Button className={classes.filter} variant="outlined" color="secondary">Specialty: All<Icon>expand_more</Icon></Button>
        <Button className={classes.filter} variant="outlined" color="secondary">Treatment: All<Icon>expand_more</Icon></Button>
        {this.state.openFilter && <Box>Filter Options</Box> }
      </Box>
  )
}

export default FilterBar
