import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect'
import SelectMultiple from './SelectMultiple'
import Button from '@material-ui/core/Button'
import Icon from '@material-ui/core/Icon'
import SelectOptions from './SelectOptions'

const useStyles = makeStyles(theme => ({
  root: {
    background:'#fff',
    padding:15,
    borderBottom: "1px solid #ddd",
    position:'relative',
  },
  Button: {
    marginRight:15,
  }
}));

const Filters = props => {
  const classes = useStyles()

  return (
    <Box className={classes.root}>
      <Button className={classes.Button} onClick={() => props.showOptions("clientType")} color="secondary" variant="contained">`Client: ${props.filters.clientTypes}`<Icon>expand_more</Icon></Button>
      <SelectOptions options={props.catalogue.clientTypes} visible={(props.visibleOptions === "clientType")} filter="clientTypes" name="clientType" onChange={props.onChange} type="radio"/>
    </Box>
  )
}

export default Filters
