import Button from "@material-ui/core/Button"
import IconButton from "@material-ui/core/IconButton"
import SelectOptions from './SelectOptions'
import Box from '@material-ui/core/Box'
import { makeStyles } from '@material-ui/core/styles'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  container: {
    display:"inline-block",
    position:"relative",
  },
  button: {
    marginRight:15,
    minWidth: 200,
    textAlign:"left",
    borderRadius:20,
    fontSize:13,
    padding:"2px 25px 2px",
    justifyContent:"normal",

  },
  expandIcon: {
    position: "absolute",
    right: 8,
  },
}));

const Filter = props => {
  const classes = useStyles()

  return (
    <Box className={classes.container}>
      <Button
        className={classes.button}
        onClick={() => props.showOptions(props.filter)}
        size="small"
        color="secondary"
        variant={props.activeFilter ? "contained" : "none"}
      >
        { props.activeFilter ?
          `${props.label}: ${props.activeFilter}` :
          `Choose a ${props.label}`
        }
        <Icon className={classes.expandIcon}>expand_more</Icon>
      </Button>

      <SelectOptions options={props.options} visible={props.visible} filter={props.filter} onChange={props.onChange} type="radio"/>
    </Box>
    )
}

export default Filter
