import { useState } from 'react'
import { makeStyles } from '@material-ui/core'
import Box from '@material-ui/core/Box'
import Option from './Option'

const useStyles = makeStyles(theme => ({
  selectOptions: {
    minWidth: 200,
    background:'#fff',
    borderRadius:4,
    border: "1px solid #ddd",
    boxShadow: '0px 5px 8px -3px rgba(0,0,0,0.14)',
    position:'absolute',
    top:40,
    zIndex:1000,
    display:'none',
    fontFamily:'raleway',
    fontSize:16,
    fontWeight:'500'
  },
  visible: {
    display:'block',
  }
}));

const options = props.options.unshift("Any")

const Select = props => {
  const classes = useStyles()

  return (
    <form onChange={(e) => props.onChange(props.filter, e.target.value)} className={`${classes.selectOptions} ${props.visible && classes.visible} `}>
      {options.map((option, i) => (
        <Option id={i} value={option} name={props.filter} type={props.type} isSelected={props.selected}>{option}</Option>
      ))}
    </form>
  )
}

export default Select
