import { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Button from '@material-ui/core/Button'
import IconButton from '@material-ui/core/IconButton'
import CloseIcon from '@material-ui/icons/Close'

const useStyles = makeStyles({
  root: {
    display: "flex",
    justifyContent: "center",
    padding: 80,
    flexWrap: "wrap"

  },
  field: {
    display: "block",
    padding: "10px 0",
    fontSize: "18px",
    fontWeight: "600"

  },
  input: {
    borderRadius:4,
    background:"#f4f6f8",
    border:"none",
    padding:'12px 30px',
    fontSize:'22px'
  },
  label: {
    display: "block",
  },
  header: {
    display: 'block',
    width: '100%',
    textAlign: 'center'
  },
  close: {
    position:"absolute",
    top:20,
    right:20,
  },
  submit: {
    width:"100%",
    paddingY:"",
    marginTop:"20px"
  }

})



const RequestListing = props => {
  const classes = useStyles()
  const [state, setState] = useState({
    name: "",
    email: ""
  })

  const handleChange = e => {
    setState({...state,
    [e.target.name]: e.target.value
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    let url = process.env.API_BASE_URL + '/request-listing'
    let response =  fetch(url, {
      method: 'POST',
      headers: {
      'Content-Type': 'application/json',
      'Token': process.env.API_TOKEN
      },
      body: JSON.stringify(state),
    })
  }

  return (
    <div className={classes.root}>
      <div className={classes.header}>
        <h2>It's Free to Join Charlotte Therapists Before 2021</h2>
        <h3 style={{fontWeight:"500"}}>Are you a therapist in the Charlotte area? Submit this form to add your listing to this site.</h3>
      </div>

      <form  onSubmit={e => handleSubmit(e)}>

        <label className={classes.field}>
          <span className={classes.label}>Your Name</span>
          <input className={classes.input} name="name" type="text" value={state.name} onChange={e => handleChange(e)}/>
        </label>

        <label className={classes.field}>
          <span className={classes.label}>Your Email Address</span>
          <input className={classes.input} name="email" type="text" value={state.email} onChange={e => handleChange(e)}/>
        </label>

        <Button size="large" className={classes.submit} color="primary" type="submit" variant="contained">Submit</Button>
      </form>
    </div>
  )
}

export default RequestListing
