import { makeStyles } from '@material-ui/core/styles'
import Filter from './Filter'
import ChevronRightIcon from '@material-ui/icons/ChevronRight'

const useStyles = makeStyles(theme => ({
  root: {
  },
  header: {
    fontSize: 30,
    paddingTop:20,
  },
  row: {
    height: 80,
    borderBottom: "1px solid #ddd",
    fontSize: 20,
    lineHeight:"80px"
  },
  label: {
    verticalAlign: 'middle',
  },
  chevronRightIcon : {
    float:'right',
    verticalAlign: 'middle',
    marginTop:24,
    fontSize:40,

  }
}))

const FiltersMobile = props => {
const classes = useStyles()


  return (
    <div className={classes.root}>
      <h2 className={classes.header}>Filters</h2>
      <div className={classes.row} onClick={() => props.show('clientType')}>
        <div>
          <span className={classes.label}>Client Type</span>
          <ChevronRightIcon  className={classes.chevronRightIcon } />
        </div>
      </div>
      <div className={classes.row}>
        <div>
          <span className={classes.label}>Specialty</span>
          <ChevronRightIcon  className={classes.chevronRightIcon } />
        </div>
      </div>
      <div className={classes.row}>
        <span className={classes.label}>Insurance</span>
        <ChevronRightIcon  className={classes.chevronRightIcon } />
      </div>
    </div>
  )
}

export default FiltersMobile
