export const Capitalize = string => {
  return string[0].toUpperCase() + string.slice(1)
}

export const CapitalizeArray = array => {
  const newArray = array.map(string => {
    return string[0].toUpperCase() + string.slice(1)
  })
  console.log(newArray)
}
