import { createMuiTheme } from '@material-ui/core/styles';
import red from '@material-ui/core/colors/red';

// Create a theme instance.
const theme = createMuiTheme({
  spacing: 8,
  palette: {
    primary: {
      main: '#556cd6',
    },
    secondary: {
      main: '#56A296',
    },
    error: {
      main: red.A400,
    },
    background: {
      default: '#fff',
    },
  },
  typography: {
    fontFamily: [
      'PT Serif',
      'Serif'
    ],
    h4: {
      fontFamily: "Raleway",
      fontSize: 13,
      fontWeight: "bold",
      color: "gray"
    }
  }
});

export default theme;
