import Button from "@material-ui/core/Button"
import SelectOptions from './SelectOptions'
import Box from '@material-ui/core/Box'
import { makeStyles } from '@material-ui/core/styles'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  Button: {
    marginRight:15,
  }
}));

const Filter = props => {
  const classes = useStyles()

  return (
    <Box>
      <Button size="small" className={classes.Button} onClick={() => props.showOptions("clientType")} color="secondary" variant="contained">Client: {props.activeFilter}<Icon>expand_more</Icon></Button>
      <SelectOptions options={props.options} visible={(props.visibleOptions === "clientType")} filter={props.filter} onChange={props.onChange} type="radio"/>
    </Box>
    )
}

export default Filter
