import React, { useState } from "react"
import { makeStyles } from '@material-ui/core/styles'
import ListItem from "@material-ui/core/ListItem"
import ListItemText from "@material-ui/core/ListItemText"
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'
import CardMedia from '@material-ui/core/CardMedia'
import Box from '@material-ui/core/Box'
import Divider from '@material-ui/core/Divider'
import School from '@material-ui/icons/School'
import Work from '@material-ui/icons/Work'
import Group from '@material-ui/icons/Group'
import Grid from '@material-ui/core/Grid'
import AttachMoney from '@material-ui/icons/AttachMoney'


const Listing = props => {
  const classes = useStyles()

  function handleClick() {
    props.setSelected(props.id)
    if (!props.showMidbar)props.setShowMidbar(true)
  }

  return (
      <li className={classes.root} button selected={props.therapist.id == props.selected ? true : false } onClick={handleClick}>
        <div className={classes.imageCropper}>
          <img src={'/therapist-images/' + props.therapist.image} className={classes.image} />
        </div>

        <div className={classes.content}>
          <Box py={1} >
            <h2>{props.therapist.first_name} {props.therapist.last_name}</h2>
            <p className={classes.title}>{props.therapist.title}</p>
          </Box>
        </div>
      </li>
  )
}

const useStyles = makeStyles(theme => ({
  root: {
    overflow: 'hidden',
    background: "white",
    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
    borderRadius: 12,
    margin: '10px 0px',
    listStyleType: 'none',
  },
  title: {
    margin: 0,
  },
  content: {
    display:'inline-block',
    height: 110,
    verticalAlign: 'middle',
    overflow:'hidden'
  },
  image: {
    display: 'inline',
    margin: '0 auto',
    marginRight: '-25%',
    height: 'auto',
    width: '100%',
  },
  imageCropper: {
    display:'inline-block',
    width: 85,
    height: 85,
    minWidth: 85,
    position: 'relative',
    overflow: 'hidden',
    borderRadius: '50%',
    marginRight:20,
  },
  chip: {
    position:'absolute',
    top:15,
    right:15,
  },

}));

export default Listing
