import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect'
import SelectMultiple from './SelectMultiple'
import Button from '@material-ui/core/Button'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    height:70,
    background: "transparent",
    padding:15,
  },
  Button: {
    paddingRight:15,
  }
}));

const Filters = props => {
  const classes = useStyles()

  return (
    <Box className={classes.root}>
      <Button color="secondary" variant="outlined">Client: All <Icon>expand_more</Icon></Button>
      <Button color="secondary" variant="outlined">Specialty: All <Icon>expand_more</Icon></Button>
      <Button color="secondary" variant="outlined">Treatment: All <Icon>expand_more</Icon></Button>
      <Button color="secondary" variant="outlined">Price: All <Icon>expand_more</Icon></Button>
    </Box>
  )
}

export default Filters


