import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect'
import SelectMultiple from './SelectMultiple'
import Button from '@material-ui/core/Button'
import SelectOptions from './SelectOptions'
import Filter from './Filter'
import Icon from '@material-ui/core/Icon'

const useStyles = makeStyles(theme => ({
  root: {
    background:'#fff',
    padding:"0 15px 15px",
    borderBottom: "1px solid #ddd",
    position:'relative',
  },
  filterIcon: {
    verticalAlign:"middle",
  }
}));

const Filters = props => {
  const classes = useStyles()

  return (
    <Box className={classes.root}>
      <Icon className={classes.filterIcon}>filter_list</Icon>
      <Filter activeFilter={props.activeFilters.clientTypes} options={props.catalogue.clientTypes} showOptions={props.showOptions} visible={(props.visibleOptions === "clientTypes")} filter="clientTypes" onChange={props.onChange} label="Client" type="radio"/>
      <Filter activeFilter={props.activeFilters.specialties} options={props.catalogue.specialties} showOptions={props.showOptions} visible={(props.visibleOptions === "specialties")} filter="specialties" onChange={props.onChange} label="Specialties" type="radio"/>
  </Box>
  )
}

export default Filters
