import { makeStyles } from '@material-ui/core'
import Box from '@material-ui/core/Box'

const useStyles = makeStyles(theme => ({
  option: {
    width: "100%",
    padding:15,
    cursor: "pointer",
    display:"block",
    "&:hover":{
      background: '#ccc',
    },
  },
  selected: {
    color: "blue",
  },
}));

const Option = props => {
  const classes = useStyles()

  return (
    <label className={`${classes.option} ${classes.selected}`} name="clientType">
      <input type={props.type} name="clientType" />
      <span>Individual</span>
    </label>
  )
}

export default Option
