import { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Container from '@material-ui/core/Container'
import Box from '@material-ui/core/Box'
import SingleSelect from './SingleSelect'
import SelectMultiple from './SelectMultiple'
import Button from '@material-ui/core/Button'
import SelectOptions from './SelectOptions'
import Filter from './Filter'
import Icon from '@material-ui/core/Icon'
import IconButton from '@material-ui/core/IconButton'

const useStyles = makeStyles(theme => ({
  root: {
    background:'#fff',
    padding:"0 15px 15px 70px",
    borderBottom: "1px solid #ddd",
    position:'relative',
  },
  filterButton: {
    height:40,
    width:40,
    margin:"0 10px",
    position:"absolute",
    left:10,
    top:-8,
    zIndex:1000,

  },
  filterIcon: {
    verticalAlign:"middle",
    color:"#777",
    fontSize:26,
  },
  active: {
    color: "#4d8ac7",
  }
}));

const anyActiveFilters = activeFilters => {
  for (let key in activeFilters) {
    if (activeFilters[key] != "") {
      return true
    }
  }
  return false
}


const Filters = props => {
  const classes = useStyles()

  return (
    <Box className={classes.root}>
      <IconButton onClick={props.clearFilters} className={classes.filterButton}><Icon className={`${classes.filterIcon} ${anyActiveFilters(props.activeFilters) && classes.active} `}>filter_list</Icon></IconButton>
      <span style={{fontFamily:"Raleway", fontWeight:"bold",marginRight:20}}>{anyActiveFilters(props.activeFilters) ? "FILTERS ON" : "FILTERS OFF"}</span>
      <Filter activeFilter={props.activeFilters.clientTypes} options={props.catalogue.clientTypes} showOptions={props.showOptions} visible={(props.visibleOptions === "clientTypes")} filter="clientTypes" onChange={props.onChange} label="Client Type" type="radio"/>
      <Filter activeFilter={props.activeFilters.specialties} options={props.catalogue.specialties} showOptions={props.showOptions} visible={(props.visibleOptions === "specialties")} filter="specialties" onChange={props.onChange} label="Specialty" type="radio"/>
    </Box>
  )
}

export default Filters
